actionConfiguration.body:
() => {
LogInAPI.run(async () => {
	const authToken = LogInAPI.data.data.token.toString();
	const userId = LogInAPI.data.data.userId.toString();
	if(authToken){
		storeValue("authToken",authToken);
		storeValue("userId",userId);
		storeValue('BucketName','sandbox-itfs-kyc-docs');
		storeValue('FilePath','kycfiles');
		await selectRoleofCurrentUser.run({ username : appsmith.store.username  })
		//showAlert(appsmith.store.username);
		const UserRole = selectRoleofCurrentUser.data[0].Role
		showAlert(UserRole);
		storeValue("userRole",UserRole)
	navigateTo('Homepage');
 }		else{ 
			showAlert('Invalid Username,password','error');
			}
}
						)}

