export default {
	docShortForms: {
		'POI': 'Proof of Identification',
		'POA': 'Proof of Address',
		'PAN': 'PAN',
		'CC': 'Cancelled Cheque',
		'IEC': 'Import Export Certificate',
		'PD': 'PartnerShip Deed',
		'PL': 'Partnership Letter',

		'COI': 'Certificate of Incorporation',
		'MAA':'Memorandum & Article of Association',
		'LDIR': 'List of Directors with DIN',
		'BR': 'Board Resolution',
		'BOD': 'Beneficial ownership declaration',

		'PD1': 'Entity Proof Document 1',
		'PD2':'Entity Proof Document 2',
		'PHT': 'Photo',

		'LPDPIN': 'List of Partners with DPIN',
		'LLPBR': 'LLP Board Resolution'
	},
	getTableData: () => {
		return ListKYCDocQuery.data.map((entry) => {
			const dirs = entry.fileName.split("/");
			const fileDetails = dirs[2].split("__");
			return {
				DocumentType: this.docShortForms[fileDetails[0]],
				DocumentName: fileDetails.length === 3 ? fileDetails[1] : '-',
				FileName: fileDetails[fileDetails.length - 1],
				Download: '',
				ApiVarificaion: '',
				VerificationStatus: '',
				path: entry.fileName,
				docTypeCode: fileDetails[0],
				docName: fileDetails.length === 3 ? fileDetails[1] : null,
				verFilePrefix: entry.fileName.split("__")[0].replace("_", "_VER_")
			};
		});
	},

	downloadFile: async (fileName, path) => {
		showAlert(`File name for download: ${fileName} ${path}`);
		const name = fileName;
		await storeValue('tempfileName', fileName);
		await downloadFileQuery.run(()=> {
				download(atob(downloadFileQuery.data.fileData), appsmith.store.tempfileName);
			}, undefined, {
			path: path
		});
	},

	getVerKYCDocName: () => {
		const meta = selectVeriStatus.selectedOptionValue ? `${selectVeriStatus.selectedOptionValue}__`: '';
		return Table1.selectedRow.verFilePrefix + '__' + meta + FilePicker1.files[0].name;
	},

	onSaveAndUpdateVer: async () => {
		await insertVerificationStatus.run();
		await uploadVerKYCDocQuery.run();
		await listVerDocData.run();
		await resetWidget('Form1', true);
	},
	getVerTableData: () => {
		return listVerDocData.data.map((e) => ({
				DocumentType: this.docShortForms[e.KYC_DOCUMENT_TYPE],
				Status: e.KYC_VERIFICATION_STATUS === 's' ? 'Success' : 'Failure',
				Comments: e.KYC_VERIFICATION_COMMENTS,
				VerificationFileName: e.KYC_VERIFICATION_FILENAME,
				Download: '',
				Remove: '',
				path: e.S3_FILE_NAME,
				docTypeCode: e.KYC_DOCUMENT_TYPE,
				statusCode: e.KYC_VERIFICATION_STATUS,
				id: e.ID
		}));
	},
	removeFile: async (path, id) => {
		await deleteVarificationStatus.run(undefined, undefined, {
			id: id
		});
		await DeleteFileQuery.run(undefined, undefined, {
			path: path
		});
		await listVerDocData.run();
	}

}
