SELECT CUSTOMER_OID, count(OID) as authsigncount, sum(CUSTOMER_KEY_PERSON_PARTNER_PERC) as authsignsum 
FROM APS_CUSTOMER_ENTITY_PERSON_DETAILS 
WHERE CUSTOMER_OID = {{appsmith.store.customerOID}}
AND (((CUSTOMER_KEY_PERSON_ENTITY_TYPE = {{'Key Person India'}} AND CUSTOMER_KEY_PERSON_TYPE = {{'partner'}}) AND CUSTOMER_KEY_PERSON_IS_AUTH_SIGN = 1) OR CUSTOMER_KEY_PERSON_ENTITY_TYPE = {{'Auth Signatory Person India'}}
OR CUSTOMER_KEY_PERSON_IS_AUTH_SIGN = 1 )
GROUP BY CUSTOMER_OID
